import React, { useState, useEffect } from "react";
import sanityClient from "@sanity/client";
import searchIcon from '../assets/image.png'; // Adjust the path accordingly

// Initialize Sanity client
const client = sanityClient({
  projectId: "x98ssp32", // Replace with your Sanity project ID
  dataset: "production", // Dataset name
  apiVersion: "2021-08-31", // API version date
  useCdn: true, // Use CDN for faster response times
});

const SearchWindow = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState("");
  const [error, setError] = useState(""); // Handle any potential errors

  // Fetch from Sanity when query changes
  useEffect(() => {
    const fetchEntries = async () => {
      if (query.length > 0) {
        const queryStr = `*[_type in ["dentalTopics", "faq", "articles"] && (title match "${query}*" || question match "${query}*")]{
          title,
          question,
          description,
          content,
          answer,
          _id
        }`;

        try {
          const results = await client.fetch(queryStr);
          setResults(results);
          setError(""); // Clear any errors when fetch is successful
        } catch (error) {
          console.error("Error fetching from Sanity:", error);
          setError("Error fetching results, please try again later.");
        }
      } else {
        setResults([]);
      }
    };

    fetchEntries();
  }, [query]);

  const fetchFromGroqBackend = async () => {
    if (!query) return; // Prevent empty queries from being sent

    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/groq", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query }),
      });

      const data = await response.json();
      const aiResult = {
        _id: "ai-response",
        title: query, // The query becomes the title of the AI response
        description: data.aiResponse || "No valid response from AI.",
      };

      // Append AI response to the existing results
      setResults((prevResults) => [...prevResults, aiResult]);
      setError(""); // Clear error on success
    } catch (error) {
      console.error("Error fetching AI response from backend:", error);
      setError("Error fetching AI response, please try again later.");
    }
    setLoading(false);
  };

  // Handle search submission on Enter or button click
  const handleSearch = async () => {
    if (query.trim() === "") return; // Ensure the query is not empty or just spaces
    await fetchFromGroqBackend();
  };

  // Handle pressing "Enter" key in the input
  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  // Open modal for displaying content
  const openModal = (content) => {
    setModalContent(content);
    setIsModalOpen(true);
  };

  // Close modal
  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent("");
  };

  return (
    <div className="flex flex-col items-center justify-center mt-10">
      <div className="relative w-full max-w-lg">
        {/* Input and Submit Button in One Box */}
        <div className="flex items-center border border-indigo-500 rounded-lg shadow-md focus-within:ring-2 focus-within:ring-indigo-400 transition duration-300 ease-in-out bg-tranquil-mist">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            onKeyDown={handleKeyDown}  // Handle pressing Enter
            className="w-full p-4 outline-none bg-transparent"
            placeholder="Search dental topics..."
            aria-label="Search for dental topics" // For screen readers
          />

          {/* Loading Spinner */}
          {loading && (
            <div className="flex items-center justify-center">
              <div className="mr-2 animate-spin rounded-full h-6 w-6 border-t-2 border-b-2 border-indigo-500"></div>
            </div>
          )}

          <button
            onClick={handleSearch}
            className="px-4 py-2 bg-royal-velvet text-white rounded-r-lg hover:bg-indigo-600 transition duration-300 transform hover:scale-105"
            aria-label="Submit Search" // For screen readers
          >
            {/* Display PNG as an Icon - Dental Magnifying Glass */}
            <img src={searchIcon} alt="Search Icon" className="h-10 w-10" /> {/* Adjusted size to be slightly bigger */}
          </button>
        </div>

        {/* Search Results (Sanity and AI Combined) */}
        {results.length > 0 && (
          <ul className="max-h-80 overflow-y-auto absolute z-10 w-full mt-4 bg-crystal-snow border border-indigo-200 rounded-lg shadow-lg">
            {results.map((item) => (
              <li
                key={item._id}
                className="p-4 border-b border-gray-100 cursor-pointer hover:bg-serene-sky transition duration-300 ease-in-out"
                onClick={() => openModal(`${item.title || item.question}\n\n${item.description || item.content || item.answer}`)}
              >
                <strong className="text-indigo-900">{item.title || item.question}</strong>
              </li>
            ))}
          </ul>
        )}

        {/* Display Error if Any */}
        {error && <p className="mt-4 text-red-500">{error}</p>}
      </div>

      {/* Modal for displaying selected content */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center z-50 overflow-y-auto">
          <div className="bg-white rounded-lg p-6 max-w-lg shadow-lg text-gray-800 relative animate-fade-in max-h-screen overflow-y-auto">
            <button
              onClick={closeModal}
              className="absolute top-0 right-0 mt-4 mr-4 text-gray-500 hover:text-gray-700 transition"
            >
              &#10005; {/* Close button for the modal */}
            </button>
            <div className="text-lg">
              <h2 className="text-indigo-900 text-2xl font-bold mb-2">Details</h2>
              <p className="text-gray-700 whitespace-pre-line">{modalContent}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchWindow;
