import React, { useState, useEffect } from "react";
import sanityClient from "@sanity/client";

// Initialize Sanity client
const client = sanityClient({
  projectId: "x98ssp32", // Replace with your Sanity project ID
  dataset: "production", // Dataset name
  apiVersion: "2021-08-31", // API version date
  useCdn: true, // Use CDN for faster response times
});

const SearchWindow = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [aiResponse, setAiResponse] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch from Sanity when query changes
  useEffect(() => {
    const fetchEntries = async () => {
      if (query.length > 0) {
        const queryStr = `*[_type in ["dentalTopics", "faq", "articles"] && (title match "${query}*" || question match "${query}*")]{
          title,
          question,
          description,
          content,
          answer,
          _id
        }`;

        try {
          const results = await client.fetch(queryStr);
          setResults(results);
        } catch (error) {
          console.error("Error fetching from Sanity:", error);
        }
      } else {
        setResults([]);
      }
    };

    fetchEntries();
  }, [query]);

  const fetchFromGroqBackend = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/groq", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ query }),
      });

      const data = await response.json();
      setAiResponse(data.aiResponse || "No valid response from Groq.");
    } catch (error) {
      console.error("Error fetching response from backend:", error);
      setAiResponse("Error fetching response from Groq.");
    }
    setLoading(false);
  };

  const fetchLLMResponse = async () => {
    await fetchFromGroqBackend(); // Call the Groq API via backend
  };

  const handleSearch = async () => {
    await fetchLLMResponse();
  };

  return (
    <div className="flex flex-col items-center justify-center mt-10">
      <div className="relative w-full max-w-lg">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="w-full p-4 border border-indigo-500 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-indigo-400 transition duration-300 ease-in-out hover:shadow-lg bg-tranquil-mist"
          placeholder="Search dental topics..."
        />
        <button
          onClick={handleSearch}
          className="mt-4 px-4 py-2 bg-royal-velvet text-white rounded-lg shadow-md hover:bg-indigo-600 transition duration-300 transform hover:scale-105"
        >
          Get AI Response
        </button>

        {results.length > 0 && (
          <ul className="absolute z-10 w-full mt-4 bg-crystal-snow border border-indigo-200 rounded-lg shadow-lg">
            {results.map((item) => (
              <li
                key={item._id}
                className="p-4 border-b border-gray-100 cursor-pointer hover:bg-serene-sky transition duration-300 ease-in-out"
              >
                <strong className="text-indigo-900">{item.title || item.question}</strong>
                <br />
                <span className="text-gray-700">{item.description || item.content || item.answer}</span>
              </li>
            ))}
          </ul>
        )}

        {aiResponse && (
          <div className="mt-6 p-6 bg-tranquil-mist border border-gray-300 rounded-lg shadow-lg animate-fade-in">
            <strong className="text-indigo-800">AI Response:</strong> <br />
            <p className="text-gray-800">{aiResponse}</p>
          </div>
        )}

        {loading && <p className="mt-4 text-indigo-700 animate-pulse">Loading AI response...</p>}
      </div>
    </div>
  );
};

export default SearchWindow;
