import React, { useState } from "react";
import sanityClient from "@sanity/client";
import Groq from "groq-sdk";

// Initialize Sanity client
const client = sanityClient({
  projectId: "x98ssp32", // Replace with your Sanity project ID
  dataset: "production", // Dataset name
  apiVersion: "2021-08-31", // API version date
  useCdn: true, // Use CDN for faster response times
});

// Initialize Groq client
const groq = new Groq({ apiKey: process.env.GROQ_API_KEY });

const SearchWindow = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [aiResponse, setAiResponse] = useState("");
  const [loading, setLoading] = useState(false);

  // Function to fetch from Sanity using GROQ
  const fetchEntries = async (searchQuery) => {
    const query = `*[_type in ["dentalTopics", "faq", "articles"] && (title match "${searchQuery}*" || question match "${searchQuery}*")]{
      title,
      question,
      description,
      content,
      answer,
      _id
    }`;

    const results = await client.fetch(query);
    setResults(results);
    return results;
  };

  // Function to fetch response from OpenAI GPT
  const fetchFromOpenAI = async (searchQuery) => {
    setLoading(true);
    const response = await fetch("https://api.openai.com/v1/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${process.env.OPENAI_API_KEY}`,
      },
      body: JSON.stringify({
        model: "gpt-4",
        prompt: `You are an AI assistant for dental health. Answer the following question: ${searchQuery}`,
        max_tokens: 100,
      }),
    });
    const data = await response.json();
    setAiResponse(data.choices[0].text.trim());
    setLoading(false);
  };

  // Function to fetch response from Groq LLM
  const fetchFromGroq = async (searchQuery) => {
    setLoading(true);
    await groq.chat.completions
      .create({
        messages: [
          {
            role: "user",
            content: searchQuery,
          },
        ],
        model: "mixtral-8x7b-32768", // Example model
      })
      .then((chatCompletion) => {
        setAiResponse(chatCompletion.choices[0]?.message?.content || "");
      })
      .catch((error) => {
        console.error("Error from Groq:", error);
      });
    setLoading(false);
  };

  // Choose which LLM to use based on API keys
  const fetchLLMResponse = async (searchQuery) => {
    if (process.env.GROQ_API_KEY) {
      await fetchFromGroq(searchQuery); // Use Groq if API key is available
    } else if (process.env.OPENAI_API_KEY) {
      await fetchFromOpenAI(searchQuery); // Fallback to OpenAI if no Groq API key
    } else {
      setAiResponse("No AI service available.");
    }
  };

  // Handle the search input
  const handleSearch = async (event) => {
    const value = event.target.value;
    setQuery(value);

    if (value.length > 0) {
      const results = await fetchEntries(value);

      // If no results are found in Sanity, pass the query to the LLM
      if (results.length === 0) {
        await fetchLLMResponse(value);
      } else {
        setAiResponse(""); // Clear AI response if Sanity has results
      }
    } else {
      setResults([]);
      setAiResponse("");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center mt-10">
      <div className="relative w-full max-w-lg">
        <input
          type="text"
          value={query}
          onChange={handleSearch}
          className="w-full p-4 border border-blue-300 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300 ease-in-out hover:shadow-lg bg-blue-50"
          placeholder="Search dental topics..."
        />
        {results.length > 0 && (
          <ul className="absolute z-10 w-full mt-2 bg-white border border-gray-200 rounded-lg shadow-lg">
            {results.map((item) => (
              <li key={item._id} className="p-3 border-b border-gray-100 cursor-pointer hover:bg-blue-100 transition duration-300 ease-in-out">
                <strong>{item.title || item.question}</strong> <br />
                <span>{item.description || item.content || item.answer}</span>
              </li>
            ))}
          </ul>
        )}

        {aiResponse && (
          <div className="mt-4 p-4 bg-gray-100 border border-gray-300 rounded-lg shadow-md">
            <strong>AI Response:</strong> <br />
            <p>{aiResponse}</p>
          </div>
        )}

        {loading && <p>Loading AI response...</p>}
      </div>
    </div>
  );
};

export default SearchWindow;
