import React, { useState } from "react";
import sanityClient from "@sanity/client";

const client = sanityClient({
  projectId: "your_project_id", // Replace with your Sanity project ID
  dataset: "production", // Dataset name
  useCdn: true, // Use CDN for fast performance
});

const SearchWindow = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);

  const handleSearch = (event) => {
    const value = event.target.value;
    setQuery(value);

    if (value.length > 0) {
      fetchEntries(value);
    } else {
      setResults([]);
    }
  };

  const fetchEntries = async (searchQuery) => {
    const query = `*[_type == "your_document_type" && title match "${searchQuery}*"]{
      title,
      _id
    }`;

    const results = await client.fetch(query);
    setResults(results);
  };

  return (
    <div className="flex flex-col items-center justify-center mt-10">
      <h2 className="text-xl font-bold">Test Component Visibility</h2>
      <div className="relative w-full max-w-lg">
        <input
          type="text"
          value={query}
          onChange={handleSearch}
          className="w-full p-4 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-lightblue-500 transition duration-300 ease-in-out hover:shadow-lg bg-lightblue-50"
          placeholder="Search dental topics..."
        />
        {results.length > 0 && (
          <ul className="absolute z-10 w-full mt-2 bg-white border border-gray-200 rounded-lg shadow-lg">
            {results.map((item) => (
              <li
                key={item._id}
                className="p-3 border-b border-gray-100 cursor-pointer hover:bg-lightblue-100 transition duration-200"
              >
                {item.title}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default SearchWindow;
